"-- Support Tickets TableCREATE TABLE support_tickets (    ticket_id INT AUTO_INCREMENT PRIMARY KEY,    ticket_number VARCHAR(20) UNIQUE NOT NULL,    officer_id INT NOT NULL,    subject VARCHAR(255) NOT NULL,    category ENUM('account', 'reporting', 'technical', 'feature', 'other') NOT NULL,    description TEXT NOT NULL,    priority ENUM('low', 'medium', 'high', 'critical') NOT NULL DEFAULT 'medium',    status ENUM('open', 'in_progress', 'on_hold', 'closed', 'resolved') NOT NULL DEFAULT 'open',    assigned_to INT,    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,    updated_at DATETIME ON UPDATE CURRENT_TIMESTAMP,    resolved_at DATETIME,    resolution_notes TEXT,    FOREIGN KEY (officer_id) REFERENCES officers(id) ON DELETE CASCADE);-- Support Ticket Attachments TableCREATE TABLE support_ticket_attachments (    id INT AUTO_INCREMENT PRIMARY KEY,    ticket_id INT NOT NULL,    file_name VARCHAR(255) NOT NULL,    file_path VARCHAR(255) NOT NULL,    file_type VARCHAR(100) NOT NULL,    file_size INT NOT NULL,    uploaded_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,    FOREIGN KEY (ticket_id) REFERENCES support_tickets(ticket_id) ON DELETE CASCADE);-- Support Ticket Comments TableCREATE TABLE support_ticket_comments (    comment_id INT AUTO_INCREMENT PRIMARY KEY,    ticket_id INT NOT NULL,    user_id INT NOT NULL,    user_role ENUM('officer', 'admin', 'support_staff') NOT NULL,    comment TEXT NOT NULL,    is_internal BOOLEAN DEFAULT FALSE,    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,    FOREIGN KEY (ticket_id) REFERENCES support_tickets(ticket_id) ON DELETE CASCADE);-- Support Ticket History TableCREATE TABLE support_ticket_history (    id INT AUTO_INCREMENT PRIMARY KEY,    ticket_id INT NOT NULL,    changed_by INT NOT NULL,    action VARCHAR(255) NOT NULL,    old_value TEXT,    new_value TEXT,    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,    FOREIGN KEY (ticket_id) REFERENCES support_tickets(ticket_id) ON DELETE CASCADE);-- FAQ Categories TableCREATE TABLE faq_categories (    category_id INT AUTO_INCREMENT PRIMARY KEY,    name VARCHAR(100) NOT NULL,    description TEXT,    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,    updated_at DATETIME ON UPDATE CURRENT_TIMESTAMP);-- FAQs TableCREATE TABLE faqs (    faq_id INT AUTO_INCREMENT PRIMARY KEY,    category_id INT NOT NULL,    question TEXT NOT NULL,    answer TEXT NOT NULL,    is_published BOOLEAN DEFAULT TRUE,    display_order INT DEFAULT 0,    created_at DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,    updated_at DATETIME ON UPDATE CURRENT_TIMESTAMP,    FOREIGN KEY (category_id) REFERENCES faq_categories(category_id) ON DELETE CASCADE);" 
